// INSERT DEFINES

#define DISTANCE

uniform vec3 referencePosition;
uniform float nearDistance;
uniform float farDistance;
varying vec3 vWorldPosition;

#include <common>



// /crowd/MAT/meshPhongBuilder_ground/globals1
varying vec3 v_POLY_globals1_position;




#include <packing>
#include <uv_pars_fragment>
#include <map_pars_fragment>
#include <alphamap_pars_fragment>
#include <alphatest_pars_fragment>
#include <clipping_planes_pars_fragment>

void main () {

	#include <clipping_planes_fragment>

	vec4 diffuseColor = vec4( 1.0 );

	#include <map_fragment>
	#include <alphamap_fragment>



	// /crowd/MAT/meshPhongBuilder_ground/vec3ToFloat1
	float v_POLY_vec3ToFloat1_z = v_POLY_globals1_position.z;

	// /crowd/MAT/meshPhongBuilder_ground/smoothstep1
	float v_POLY_smoothstep1_val = smoothstep(-0.9, 1.6, v_POLY_vec3ToFloat1_z);

	// /crowd/MAT/meshPhongBuilder_ground/output1
	diffuseColor.a = v_POLY_smoothstep1_val;




	// INSERT BODY

	#include <alphatest_fragment>

	float dist = length( vWorldPosition - referencePosition );
	dist = ( dist - nearDistance ) / ( farDistance - nearDistance );
	dist = saturate( dist ); // clamp to [ 0, 1 ]

	gl_FragColor = packDepthToRGBA( dist );

}

